import * as React from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';

const bull = (
  <Box
    component="span"
    sx={{ display: 'inline-block', mx: '2px', transform: 'scale(0.8)' }}
  >
  </Box>
);


const card = (
  <>
    <CardContent>
      <Box sx={{
        width: 300,
        height: 50
      }}>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          Main Account
        </Typography>
        <Typography sx={{ fontSize: 14 }} color="text.secondary" gutterBottom>
          Wallet ID:
        </Typography>
        <Typography variant="body1" align='right'>
          {`${wallet.balance} ${wallet.currency}`}
        </Typography>
        <Typography variant="body2">
          {wallet._id}
        </Typography>

      </Box>
    </CardContent>
    <CardActions>
      <Button
        variant='contained'
        size="small"
        onClick={() => {
          setIsTransfering(false);
          setOpen(true);
        }}
      >
        Add money
      </Button>
      <Button
        type='button'
        variant='contained'
        size="small"
        onClick={() => {
          setIsTransfering(true);
          setOpen(true);
        }}
      >
        Transfer money
      </Button>
    </CardActions>
  </>
);


const wallet = {
  _id: "123123123",
  balance: 5,
  currency: 'USD'
};

export default function OutlinedCard() {
  const [open, setOpen] = React.useState(false);
  const [isTransfering, setIsTransfering] = React.useState(false);
  const [amount, setAmount] = React.useState(0);
  const [destinationWallet, setDestinationWallet] = React.useState(false);
  const [errorMessage, setErrorMessage] = React.useState("");

  const addTransaction = () => {
    console.log('New transaction', amount, destinationWallet);
  }

  return (
    <>
      <Modal
        open={false}
        setOpen={setOpen}
        title={
          isTransfering ? 'Transfer money to other wallet' : 'Add money to your wallet'
        }
        body={
          <>
            {isTransfering && (<div>
              <label htmlFor="destination">
                Destination wallet
              </label>
              <input
                type="string"
                id="destination"
                value={destinationWallet}
                onChange={(e) => setDestinationWallet(e.target.value)}
              />
            </div>)}

            <div>
              <label htmlFor="amount">
                Name
              </label>
              <input
                type="number"
                id="amount"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="0.00"
              />
            </div>
          </>
        }
        footer={
          <Button
            type='button'
            variant='contained'
            size="small"
            onClick={addTransaction}
          >
            {isTransfering ? "Transfer" : "add"}
          </Button>}
        errorMessage={errorMessage}
      />
      <Box sx={{ minWidth: 275 }}>
        <Card variant="outlined">{card}</Card>
      </Box>
    </>
  );
}

