import React from "react";
import ContactsCollection from "../api/ContactsCollection";
import { useTracker,useSubscribe} from 'meteor/react-meteor-data';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import Divider from '@mui/material/Divider';
import ListItemText from '@mui/material/ListItemText';
import ListItemAvatar from '@mui/material/ListItemAvatar';
import Avatar from '@mui/material/Avatar';
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import DeleteIcon from '@mui/icons-material/Delete';




export const ContactList = () => {
    useSubscribe()
    const contacts = useTracker(() => {
        return ContactsCollection.find({}, { sort: { createdAt: -1 } }).fetch(); //Tracker 
    });

    const removeContact = (event, _id) => {
        event.preventDefault();  
        Meteor.call('contacts.remove', { contactId:_id});
    }

    return (
        <div>
            <h3>Contact List</h3>
            <List sx={{ width: '100%', maxWidth: 360, bgcolor: 'background.paper' }}>
                {contacts.map((person, personIdx) => (
                    <ListItem alignItems="flex-start" key={personIdx}>
                        <ListItemAvatar>
                            <Avatar alt="" src={person.imageURL} />
                        </ListItemAvatar>
                        <ListItemText
                            primary={person.name}
                            secondary={
                                <React.Fragment>
                                    <Typography
                                        sx={{ display: 'inline' }}
                                        component="span"
                                        variant="body3"
                                        color="text.primary"
                                    >
                                        {person.email}
                                    </Typography>   
                                    <IconButton aria-label="delete" onClick={(event) => removeContact(event, person._id)}>
                                        <DeleteIcon />

                                    </IconButton>                                   
                                </React.Fragment>
                            }
                        ></ListItemText>
                    </ListItem>
                ))}
            </List>
        </div>
    )
}

