import React, { useState } from 'react';
import { Meteor } from 'meteor/meteor'
import ErrorAlert from './components/ErrorAlert';
import SuccessAlert from './components/SuccessAlert';
import Button from '@mui/material/Button';
import InputLabel from '@mui/material/InputLabel';
import Box from '@mui/material/Box';
import Input from '@mui/material/Input';

import InputAdornment from '@mui/material/InputAdornment';
import FormControl from '@mui/material/FormControl';
import TextField from '@mui/material/TextField';
import AccountCircle from '@mui/icons-material/AccountCircle';

export const ContactForm = () => {     
  const [name, setName] = React.useState("");  //formik
  const [email, setEmail] = React.useState("");
  const [imageURL, setImageURL] = React.useState("");
  const [walletId, setWalletId] = React.useState("");
  const [error, setError] = React.useState("");
  const [success, setSuccess] = React.useState("");

  const showError = ({ message }) => {
    setError(message);
    setTimeout(() => {
      setError("")
    }, 5000);
  }

  const showSuccess = ({ message }) => {
    setSuccess(message);
    setTimeout(() => {
      setSuccess("")
    }, 5000);
  }

  const saveContact = () => {
    Meteor.call('contacts.insert', { name, email, imageURL, walletId }, (errorResponse) => {
      if (errorResponse) {
        showError({ message: errorResponse.error })
      } else {
        setName("");
        setEmail("");
        setImageURL("");
        setWalletId("");
        showSuccess({ message: "contact save" })
      }
    });
  }

  return (
    <form>
      {error && <ErrorAlert message={error} />}
      {success && <SuccessAlert message={success} />}

      <div>
      <FormControl variant="standard">
        <InputLabel htmlFor="input-with-icon-adornment">
          With a start adornment
        </InputLabel>
        <Input
          id="input-with-icon-adornment"
          startAdornment={
            <InputAdornment position="start">
              <AccountCircle />
            </InputAdornment>
          }
        />
      </FormControl>
        {/*<label htmlFor="name">
          Name
        </label>*/}
        <input id="name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          type="text" />
      </div>
      <div>
        <label htmlFor="email">Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          id="email" />
      </div>
      <div>
        <label htmlFor="imageURL">Wallet ID</label>
        <input
          type="text"
          value={walletId}
          onChange={(e) => setWalletId(e.target.value)}
          id="imageURL" />
      </div>
      <div>
        <label htmlFor="imageURL">Image URL</label>
        <input
          type="text"
          value={imageURL}
          onChange={(e) => setImageURL(e.target.value)}
          id="imageURL" />
      </div>
      <div>
      <Button variant="contained" onClick={saveContact}>Save Contact</Button>
      </div>
    </form>
  )
}

